CREATE OR REPLACE FUNCTION obter_se_perm_config_agecons (cd_pessoa_usuario_p text, cd_perfil_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1) := 'S';
qt_regra_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_perm_config_agenda
where	cd_agenda = cd_agenda_p;

if (qt_regra_w > 0) then

	select	coalesce(max(ie_permite),'S')
	into STRICT	ie_permite_w
	from	regra_perm_config_agenda
	where	cd_agenda					= cd_agenda_p
	and	coalesce(cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p
	and	coalesce(cd_perfil, cd_perfil_p)			= cd_perfil_p;

end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
