CREATE OR REPLACE FUNCTION obter_se_perm_higienizar ( ie_acao_p text, nr_seq_interno_p bigint, cd_setor_Atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ds_ramal_p text, ds_param_Adic1_p text, ds_param_Adic2_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_acao_p
I	- inicio
F	- fim
*/
ds_Retorno_W		varchar(1)	:= 'N';
ie_Status_unidade_w	varchar(3);


BEGIN

select	max(ie_Status_unidade)
into STRICT	ie_Status_unidade_w
from	unidade_atendimento
where	nr_seq_interno	= nr_seq_interno_p;

if (ie_acao_p	= 'I') and (ie_status_unidade_w in ('R','L','A','S','G')) then
	ds_Retorno_w	:= 'S';
elsif (ie_acao_p	= 'F') and (ie_status_unidade_w in ('S','H')) then
	ds_Retorno_w	:= 'S';
end if;

return	ds_Retorno_W;

end;
$body$
