SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_perm_lib_prescr ( nr_prescricao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_obriga_proc_w	varchar(1);
ie_obriga_anamnese_w	varchar(1);
ie_anamnese_w		varchar(1);
ie_liberacao_w		varchar(1) := 'N';


BEGIN 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	begin 
	ie_obriga_proc_w	:= consiste_regra_obriga_proc(nr_prescricao_p);
	ie_obriga_anamnese_w	:= verifica_se_obriga_anamnese(nr_prescricao_p);
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_anamnese_w 
	from	anamnese_paciente 
	where	nr_atendimento	= nr_atendimento_p 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	 
	if (ie_obriga_proc_w = 'S') and 
		((ie_obriga_anamnese_w	= 'N') or (ie_anamnese_w	= 'S')) then 
		begin 
		ie_liberacao_w	:= 'S';
		end;
	else 
		begin 
		ie_liberacao_w	:= 'N';
		end;
	end if;
	end;
end if;
return	ie_liberacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
