CREATE OR REPLACE FUNCTION obter_se_perm_marc_excl (cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w         varchar(1) := 'S';
    qt_regra_w			 bigint;
	hr_dif_marc_w        bigint;

C01 CURSOR FOR

	SELECT 	nm_usuario_lib,
			cd_perfil,
			nr_horas_lib_marc
	from	ageint_regra_perm_marc
	where	coalesce(ie_situacao,'A') = 'A'
	and     cd_agenda = cd_agenda_p
	and		((trunc(dt_agenda_p) >= trunc(dt_vigencia_inicial)) or (coalesce(dt_vigencia_inicial::text, '') = ''))
	and		((trunc(dt_agenda_p) <= trunc(dt_vigencia_final))   or (coalesce(dt_vigencia_final::text, '') = ''))
	and		(to_char(dt_agenda_p, 'sssss'))::numeric  between (to_char(hr_perm_inicial, 'sssss'))::numeric  and (to_char(hr_perm_final, 'sssss'))::numeric;

BEGIN

	select 	count(*)
	into STRICT	qt_regra_w
	from	ageint_regra_perm_marc
	where	cd_agenda = cd_agenda_p
	and		coalesce(ie_situacao, 'A') = 'A'
	and		((trunc(dt_agenda_p) >= trunc(dt_vigencia_inicial)) or (coalesce(dt_vigencia_inicial::text, '') = ''))
	and		((trunc(dt_agenda_p) <= trunc(dt_vigencia_final))   or (coalesce(dt_vigencia_final::text, '') = ''))
	and		(to_char(dt_agenda_p, 'sssss'))::numeric  between (to_char(hr_perm_inicial, 'sssss'))::numeric  and (to_char(hr_perm_final, 'sssss'))::numeric;

	if (qt_regra_w	> 0) then
		ds_retorno_w := 'N';

		for c01_w in c01 loop

			hr_dif_marc_w := ((dt_agenda_p - clock_timestamp()) * 1440);

			if (c01_w.nm_usuario_lib = nm_usuario_p) or (c01_w.cd_perfil  = cd_perfil_p)  or
					((hr_dif_marc_w <= (c01_w.nr_horas_lib_marc * 60)) and c01_w.nr_horas_lib_marc > 0) then
						ds_retorno_w := 'S';
			else
					ds_retorno_w := 'N';
			end if;

		end loop;

	end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
