SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_perm_pf_classif ( cd_funcao_p bigint, cd_agenda_p bigint, cd_pessoa_fisica_p text, dt_referencia_p timestamp, ie_opcao_p text default 'S', ie_somente_pessoa_p text default 'N', dt_agenda_p timestamp default null) RETURNS varchar AS $body$
DECLARE


qt_regra_w			bigint;
qt_regra_sem_classif_w		bigint;
nr_seq_classif_w		bigint;
qt_perm_w			bigint;
nr_seq_classif_ww		bigint;
qt_regra_agenda_w		bigint;
qt_regra_perfil_w		bigint;	
cd_perfil_w			integer;
cd_especialidade_regra_w	bigint;
nr_Seq_Classif_pf_W		bigint;
qt_especialidade_w		bigint;
ds_retorno_w			varchar(80)	:= 'S';
ds_classificacao_w		varchar(80);
ds_classif_retorno_w		varchar(80);
ie_cursor_w			varchar(1)	:= 'N';
cd_especialidade_w		varchar(80);
ie_perm_espec_w			varchar(1);
ds_classif_esp_ret_w		varchar(80);
ie_consiste_classif_w		varchar(1);
ie_dia_semana_w 	varchar(3);

C01 CURSOR FOR
	SELECT	nr_seq_classif,
			nr_sequencia
	from	pessoa_classif
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((dt_referencia_p 	>= dt_inicio_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = ''))
	and	((Dt_referencia_p 	<= dt_final_vigencia) or (coalesce(dt_final_vigencia::text, '') = ''))
	order by nr_seq_classif;

C02 CURSOR FOR
	SELECT	cd_especialidade
	from	pessoa_classif_espec
	where	nr_seq_classif_pf	= nr_Seq_Classif_pf_W
	and	((dt_referencia_p 	>= dt_inicio_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = ''))
	and	((Dt_referencia_p 	<= dt_fim_vigencia) or (coalesce(dt_fim_vigencia::text, '') = ''))
	and	ie_perm_espec_w		= 'N'
	order by nr_sequencia;


BEGIN
ie_dia_semana_w	:= pkg_date_utils.get_weekday(dt_referencia_p);

cd_perfil_w := Obter_perfil_Ativo;

select	count(*)
into STRICT	qt_regra_w
from	regra_funcao_classif_pf
where	cd_funcao			= cd_funcao_p
and	coalesce(cd_perfil,cd_perfil_w) 	= cd_perfil_w;


select	max(cd_especialidade)
into STRICT	cd_especialidade_w
from	agenda
where	cd_agenda			= cd_agenda_p;

select	count(*)
into STRICT	qt_regra_agenda_w
from	regra_funcao_classif_pf
where	cd_funcao			= cd_funcao_p
and	cd_agenda 			= cd_agenda_p
and	coalesce(cd_perfil,cd_perfil_w) 	= cd_perfil_w;


select	count(*)
into STRICT	qt_regra_perfil_w
from	regra_funcao_classif_pf
where	cd_funcao			= cd_funcao_p
and	cd_perfil 			= cd_perfil_w;
	
open C01;
loop
fetch C01 into
	nr_seq_classif_w,
	nr_Seq_Classif_pf_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_cursor_w	:= 'S';
	ie_perm_espec_w	:= 'N';

	if (cd_funcao_p	 = 869) then
		if (dt_Agenda_p IS NOT NULL AND dt_Agenda_p::text <> '') then
			select	max(c.ds_classificacao)
			into STRICT	ds_classificacao_w
			from	regra_funcao_classif_pf a,
					regra_classif_pf b,
					classif_pessoa c
			where	a.nr_sequencia		= b.nr_seq_regra
			and		c.nr_sequencia		= b.nr_seq_classif
			and		a.cd_funcao		= cd_funcao_p
			and		((a.cd_perfil		= cd_perfil_w AND qt_regra_perfil_w > 0) or ((coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w) and (qt_regra_perfil_w = 0)))	
			and		((a.cd_agenda		= cd_agenda_p AND qt_regra_agenda_w > 0) or ((coalesce(a.cd_agenda, cd_agenda_p) = cd_agenda_p) and (qt_regra_agenda_w = 0)))
			and 	b.nr_seq_classif = nr_seq_classif_w
			and not exists (
						SELECT 1
						from lib_regra_classif_pf d
						where 1=1
						and (dt_Agenda_p >= to_date(to_char(dt_Agenda_p,'dd/mm/yyyy')||' '||to_char(hr_inicial,'hh24:mi'),'dd/mm/yyyy hh24:mi:ss') or coalesce(hr_inicial::text, '') = '')
							and (dt_Agenda_p <= to_date(to_char(dt_Agenda_p,'dd/mm/yyyy')||' '||to_char(hr_final,'hh24:mi'),'dd/mm/yyyy hh24:mi:ss') or coalesce(hr_final::text, '') = '')
							and (dt_referencia_p >= dt_inicio_vigencia or coalesce(dt_inicio_vigencia::text, '') = '')
							and (dt_referencia_p <=  dt_fim_vigencia or coalesce(dt_fim_vigencia::text, '') = '')
							and coalesce(ie_dia_semana, ie_dia_semana_w) = ie_dia_semana_w
							and ie_situacao = 'A'
							and b.nr_sequencia = d.nr_seq_regra_classif
						);
		else
			select	max(c.ds_classificacao)
			into STRICT	ds_classificacao_w
			from	regra_funcao_classif_pf a,
				regra_classif_pf b,
				classif_pessoa c
			where	a.nr_sequencia		= b.nr_seq_regra
			and	c.nr_sequencia		= b.nr_seq_classif
			and	a.cd_funcao		= cd_funcao_p
			and	((a.cd_perfil		= cd_perfil_w AND qt_regra_perfil_w > 0) or ((coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w) and (qt_regra_perfil_w = 0)))	
			and	((a.cd_agenda		= cd_agenda_p AND qt_regra_agenda_w > 0) or ((coalesce(a.cd_agenda, cd_agenda_p) = cd_agenda_p) and (qt_regra_agenda_w = 0)))
			and b.nr_seq_classif = nr_seq_classif_w
					and not exists (
						SELECT 1
						from lib_regra_classif_pf d
						where 1=1
							and ie_situacao = 'A'
							and b.nr_sequencia = d.nr_seq_regra_classif
						);
		end if;
	else
		select	max(c.ds_classificacao)
		into STRICT	ds_classificacao_w
		from	regra_funcao_classif_pf a,
			regra_classif_pf b,
			classif_pessoa c
		where	a.nr_sequencia		= b.nr_seq_regra
		and	c.nr_sequencia		= b.nr_seq_classif
		and	a.cd_funcao		= cd_funcao_p
		and	((a.cd_perfil		= cd_perfil_w AND qt_regra_perfil_w > 0) or ((coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w) and (qt_regra_perfil_w = 0)))	
		and	((a.cd_agenda		= cd_agenda_p AND qt_regra_agenda_w > 0) or ((coalesce(a.cd_agenda, cd_agenda_p) = cd_agenda_p) and (qt_regra_agenda_w = 0)))
		and	b.nr_seq_classif	= nr_seq_classif_w;
	end if;
	
	if (ds_classificacao_w IS NOT NULL AND ds_classificacao_w::text <> '') then
		ds_retorno_w		:= 'N';
		nr_seq_classif_ww	:= nr_seq_classif_w;
		ds_classif_retorno_w	:= ds_classificacao_w;
		ds_classif_esp_ret_w	:= substr(wheb_mensagem_pck.get_texto(299793, 'IE_CLASSIF=' || ds_classificacao_w),1,80);
	end if;

	if	(ds_retorno_w	<> 'N' AND cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '')then
		select	coalesce(max(ie_classif_pf_agenda), 'N')
		into STRICT	ie_consiste_classif_w
		from	especialidade_medica
		where	cd_especialidade	= cd_especialidade_w;

		select	count(*)
		into STRICT	qt_especialidade_w
		from	pessoa_classif_espec
		where	nr_seq_classif_pf	= nr_Seq_Classif_pf_W;

		if (ie_consiste_classif_w	= 'S') and (qt_especialidade_w	> 0) then
			open C02;
			loop
			fetch C02 into
				cd_especialidade_regra_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin
				if (cd_especialidade_regra_w	= cd_Especialidade_w) then
					ie_perm_espec_w	:= 'S';
				end if;
				end;
			end loop;
			close C02;
		else
			ie_perm_espec_w	:= 'S';
		end if;

		if (ie_perm_espec_w	= 'N') then
			ds_retorno_w		:= 'N';
			nr_seq_classif_ww	:= nr_seq_classif_w;
			ds_classif_esp_ret_w	:= substr(wheb_mensagem_pck.get_texto(299800, 'CD_ESPECIALIDADE_W=' || obter_nome_especialidade(cd_especialidade_w)),1,80);
		end if;
	end if;
	end;
end loop;
close C01;

if (ds_retorno_w = 'S') and (ie_somente_pessoa_p = 'S') then
	select	count(*)
	into STRICT	qt_regra_w
	from	regra_funcao_classif_pf
	where	cd_funcao = cd_funcao_p;
	if (qt_regra_w	> 0) then
		open C01;
		loop
		fetch C01 into
			nr_seq_classif_w,
			nr_seq_classif_pf_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			ie_cursor_w	:= 'S';

			select	max(c.ds_classificacao)
			into STRICT	ds_classificacao_w
			from	regra_funcao_classif_pf a,
				regra_classif_pf b,
				classif_pessoa c
			where	a.nr_sequencia		= b.nr_seq_regra
			and	a.cd_funcao		= cd_funcao_p
			and	c.nr_sequencia		= b.nr_seq_classif
			and	b.nr_seq_classif	= nr_seq_classif_w;

			if (ds_classificacao_w IS NOT NULL AND ds_classificacao_w::text <> '') then
				ds_retorno_w		:= 'N';
				nr_seq_classif_ww	:= nr_seq_classif_w;
				ds_classif_retorno_w	:= ds_classificacao_w;
				ds_classif_esp_ret_w	:= substr(wheb_mensagem_pck.get_texto(299793, 'IE_CLASSIF=' || ds_classificacao_w),1,80);
			end if;
			end;
		end loop;
		close C01;
	end if;
end if;

if (ie_cursor_w = 'N') then

	select	count(*)
	into STRICT	qt_regra_sem_classif_w
	from	regra_funcao_classif_pf
	where	cd_funcao = cd_funcao_p
	and	((cd_perfil = cd_perfil_w AND qt_regra_perfil_w > 0) or ((coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w) and (qt_regra_perfil_w = 0)))	
	and	coalesce(ie_consiste_pf_sem_classif,'N') = 'S';

	if (qt_regra_sem_classif_w > 0) then
		ds_classif_retorno_w := wheb_mensagem_pck.get_texto(299801);
	end if;
end if;

if (ie_opcao_p	= 'S') then
	ds_retorno_w	:= ds_retorno_w;
elsif (ie_opcao_p	= 'SEQ') then
	ds_retorno_w	:= nr_seq_classif_ww;
elsif (ie_opcao_p	= 'DS') then
	ds_retorno_w	:= ds_classif_retorno_w;
elsif (ie_opcao_p	= 'DCE') then
	ds_Retorno_w	:= ds_classif_esp_ret_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_perm_pf_classif ( cd_funcao_p bigint, cd_agenda_p bigint, cd_pessoa_fisica_p text, dt_referencia_p timestamp, ie_opcao_p text default 'S', ie_somente_pessoa_p text default 'N', dt_agenda_p timestamp default null) FROM PUBLIC;

