CREATE OR REPLACE FUNCTION obter_se_perm_proc_pac_gravida ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_proc_interno_p proc_interno.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_sexo_w	pessoa_fisica.ie_sexo%type;
ie_pac_gravida_w	historico_saude_mulher.ie_pac_gravida%type;

ds_retorno_w	varchar(1) := 'S';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
		select	a.ie_sexo
		into STRICT	ie_sexo_w
		from	pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

		if (ie_sexo_w = 'F') then
			begin
				select	coalesce(max(a.ie_pac_gravida), 'N')
				into STRICT	ie_pac_gravida_w
				from	historico_saude_mulher a
				where	nr_sequencia = (	SELECT	max(a.nr_sequencia)
							from	historico_saude_mulher a
							where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
							and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
							and	coalesce(dt_inativacao::text, '') = '');
				
				if (ie_pac_gravida_w = 'S') then
					begin
						select	coalesce(max(ie_permite_prescr_gravida), 'S')
						into STRICT	ds_retorno_w
						from	proc_interno
						where	nr_sequencia = nr_seq_proc_interno_p;
					end;
				end if;
			end;
		end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
