SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_perm_susp_item_adep ( cd_estabelecimento_p bigint, nm_usuario_p text, nr_atendimento_p bigint, ie_tipo_item_p text, cd_item_p text, nr_prescricoes_p text) RETURNS varchar AS $body$
DECLARE

 
ie_permite_w			char(1) := 'N';
ie_data_proc_w			varchar(15);
ie_data_lib_prescr_w	varchar(15);
ie_exibe_suspenso_w		varchar(15);


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and 
--	(cd_item_p is not null) and 
	(nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then 
 
	ie_exibe_suspenso_w := obter_param_usuario(1113, 117, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_exibe_suspenso_w);
 
	if (ie_tipo_item_p not in ('P','E')) then 
		ie_data_lib_prescr_w := obter_param_usuario(1113, 115, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_data_lib_prescr_w);
	end if;
	 
	if (ie_tipo_item_p in ('M', 'IAH')) then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_mat_hor c, 
				prescr_material b, 
				prescr_medica a where		c.nr_prescricao = b.nr_prescricao 
		and	c.nr_seq_material = b.nr_sequencia 
		and	b.nr_prescricao = a.nr_prescricao 
		--and	a.dt_liberacao is not null 
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	b.ie_agrupador in (1) 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	b.cd_material = cd_item_p 
		--and	c.dt_fim_horario is null 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	coalesce(c.ie_adep,'S') = 'S' 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' LIMIT 1;
 
	elsif (ie_tipo_item_p in ('P', 'I', 'C', 'G')) then 
	 
		ie_data_proc_w := obter_param_usuario(924, 223, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_data_proc_w);
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_proc_hor c, 
				prescr_procedimento b, 
				prescr_medica a where c.nr_prescricao = b.nr_prescricao 
		and	c.nr_seq_procedimento = b.nr_sequencia 
		and	b.nr_prescricao = a.nr_prescricao 
		--and	a.dt_liberacao is not null 
		and	(obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_proc_w) IS NOT NULL AND (obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_proc_w))::text <> '') 
		and	obter_se_exibir_adep_suspensos(b.dt_suspensao, ie_exibe_suspenso_w) = 'S' 
		and	coalesce(b.nr_seq_exame::text, '') = '' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	b.cd_procedimento = cd_item_p 
		and	coalesce(c.dt_fim_horario::text, '') = '' 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' LIMIT 1;
 
	elsif (ie_tipo_item_p = 'R') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_rec_hor c, 
				prescr_recomendacao b, 
				prescr_medica a where	c.nr_prescricao = b.nr_prescricao 
		and	c.nr_seq_recomendacao = b.nr_sequencia 
		and	b.nr_prescricao = a.nr_prescricao 
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	((to_char(b.cd_recomendacao) = cd_item_p) or (coalesce(b.cd_recomendacao::text, '') = '' and substr(coalesce(b.ds_recomendacao,' '),1,255) = cd_item_p) or (coalesce(b.cd_recomendacao::text, '') = '' and coalesce(b.ds_recomendacao::text, '') = '')) 
		and	coalesce(c.dt_fim_horario::text, '') = '' 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' LIMIT 1;
	 
	elsif (ie_tipo_item_p = 'E') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	pe_prescr_proc_hor c, 
				pe_prescr_proc b, 
				pe_prescricao a where c.nr_seq_pe_proc = b.nr_sequencia 
		and	b.nr_seq_prescr = a.nr_sequencia 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.nr_atendimento = nr_atendimento_p 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		--and	nvl(b.ie_adep,'N') = 'S' 
		and	coalesce(b.ie_adep,'N') in ('S','M') 
		and	b.nr_seq_proc = cd_item_p 
		and	coalesce(c.dt_fim_horario::text, '') = '' 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	obter_se_valor_contido(a.nr_sequencia, nr_prescricoes_p) = 'S' LIMIT 1;
 
	elsif (ie_tipo_item_p = 'S') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_mat_hor c, 
				prescr_material b, 
				prescr_medica a where c.nr_prescricao = b.nr_prescricao 
		and	c.nr_seq_material = b.nr_sequencia 
		and	b.nr_prescricao = a.nr_prescricao 
		--and	a.dt_liberacao is not null 
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	b.ie_agrupador in (12) 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	b.cd_material = cd_item_p 
		and	coalesce(c.dt_fim_horario::text, '') = '' 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' LIMIT 1;
		 
	elsif (ie_tipo_item_p = 'MAT') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_mat_hor c, 
				prescr_material b, 
				prescr_medica a where c.nr_prescricao = b.nr_prescricao 
		and	c.nr_seq_material = b.nr_sequencia 
		and	b.nr_prescricao = a.nr_prescricao 
		--and	a.dt_liberacao is not null 
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	b.ie_agrupador in (2) 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	b.cd_material = cd_item_p 
		and	coalesce(c.dt_fim_horario::text, '') = '' 
		and	coalesce(c.dt_suspensao::text, '') = '' 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' LIMIT 1;		
 
	elsif (ie_tipo_item_p = 'D') then 
		--ie_permite_w := 'N'; 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from (SELECT	1 
			from	prescr_dieta_hor c, 
				prescr_dieta b, 
				prescr_medica a where c.nr_prescricao = a.nr_prescricao 
			and	b.nr_sequencia = c.nr_seq_dieta 
			and	b.nr_prescricao = c.nr_prescricao 
			--and	a.dt_liberacao is not null 
			and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
			and	a.nr_atendimento = nr_atendimento_p 
			and	a.dt_validade_prescr > clock_timestamp() 
			and	coalesce(c.cd_refeicao::text, '') = '' 
			and	coalesce(c.dt_fim_horario::text, '') = '' 
			and	coalesce(c.dt_suspensao::text, '') = '' 
			and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' 
			
union
 
			SELECT	1 
			from	prescr_dieta_hor c, 
				prescr_medica a where c.nr_prescricao = a.nr_prescricao 
			--and	a.dt_liberacao is not null 
			and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
			and	a.nr_atendimento = nr_atendimento_p 
			and	a.dt_validade_prescr > clock_timestamp() 
			and	c.cd_refeicao = cd_item_p 
			and	coalesce(c.dt_fim_horario::text, '') = '' 
			and	coalesce(c.dt_suspensao::text, '') = '' 
			and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' LIMIT 1) alias14;
	elsif (ie_tipo_item_p = 'MM') then --Medicamento sem prescr_mat_hor 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_w 
		from	prescr_material b, 
				prescr_medica a where b.nr_prescricao = a.nr_prescricao		 
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S' 
		and	a.nr_atendimento = nr_atendimento_p 
		and	b.ie_agrupador in (1) 
		and	coalesce(b.ie_suspenso,'N') <> 'S' 
		and	a.dt_validade_prescr > clock_timestamp() 
		and	b.cd_material = cd_item_p		 
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_p) = 'S' LIMIT 1;
	end if;
end if;
 
return ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_perm_susp_item_adep ( cd_estabelecimento_p bigint, nm_usuario_p text, nr_atendimento_p bigint, ie_tipo_item_p text, cd_item_p text, nr_prescricoes_p text) FROM PUBLIC;

