CREATE OR REPLACE FUNCTION obter_se_pessoa_entrada_saida ( cd_pessoa_fisica_p text, cd_tipo_controle_p bigint) RETURNS varchar AS $body$
DECLARE


/*Se retornar:
N = Nunca teve entrada
E = Teve entrada e nao teve saida
S = Teve entrada e saÃ­da*/
ie_entrada_saida_w		varchar(1) := 'N';
qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	controle_pessoa_comp
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	cd_tipo_controle = cd_tipo_controle_p;

if (qt_existe_w = 0) then
	ie_entrada_saida_w := 'N';
elsif (qt_existe_w > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	controle_pessoa_comp
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_tipo_controle = cd_tipo_controle_p
	and	coalesce(dt_saida::text, '') = '';

	if (qt_existe_w > 0) then
		ie_entrada_saida_w := 'E';
	else
		ie_entrada_saida_w := 'S';
	end if;
end if;

return	ie_entrada_saida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
