CREATE OR REPLACE FUNCTION obter_se_pessoa_fisica_compras ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1);
qt_existe_w		integer;


BEGIN

ds_retorno_w	:= 'S';

select	count(*)
into STRICT	qt_existe_w
from	regra_fornecedor_compras
where	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

if (qt_existe_w > 0) then
	select	coalesce(max(ie_permite_informar),'S')
	into STRICT	ds_retorno_w
	from	regra_fornecedor_compras
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_estabelecimento = cd_estabelecimento_p;
end if;

return coalesce(ds_retorno_w, 'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
