SET client_encoding TO 'UTF8';



CREATE TYPE registro AS (
	cd_municipio sus_municipio.cd_municipio_ibge%type,
	ds_municipio sus_municipio.ds_municipio%type
);


CREATE OR REPLACE FUNCTION obter_se_pessoa_fisica_munic ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, ds_municipios_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(1); -- S or N
ds_munic_pf_w		compl_pessoa_fisica.ds_municipio%type;

type registro_v is table of registro index by integer;

registros_w registro_v;

idx integer;

municipios CURSOR FOR
SELECT  cd_municipio_ibge,
        upper(elimina_acentos(ds_municipio)) ds_municipio
from    sus_municipio
where   cd_municipio_ibge in ((WITH RECURSIVE cte AS (
select      regexp_substr(ds_municipios_p,'[^,]+', 1, level)
		    
		    (regexp_substr(ds_municipios_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ds_municipios_p, '[^,]+', 1, level))::text <> '')  UNION ALL
select      regexp_substr(ds_municipios_p,'[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
));
BEGIN

ds_retorno_w := 'N';

if 	(cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')
	and (ds_municipios_p IS NOT NULL AND ds_municipios_p::text <> '') then

	idx := 0;
	-- esse select eh rapido, nao precisamos comparar aqui dentro
	for municipio in municipios loop
		registros_w[idx].ds_municipio := municipio.ds_municipio;
		idx := idx + 1;
	end loop;

	begin
		select	ds_municipio
		into STRICT	ds_munic_pf_w
		from	compl_pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
        and     ie_tipo_complemento = 1; --Residencial
	exception when others then
		ds_munic_pf_w := null;
		ds_retorno_w := 'N';
	end;

	if	(ds_munic_pf_w IS NOT NULL AND ds_munic_pf_w::text <> '')
        and registros_w.count > 0 then

		ds_munic_pf_w := upper(elimina_acentos(ds_munic_pf_w));

		for i in registros_w.first .. registros_w.last loop
			if registros_w[i].ds_municipio like ds_munic_pf_w then
				ds_retorno_w := 'S';
				exit;
			end if;
		end loop;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
