CREATE OR REPLACE FUNCTION obter_se_pessoa_lote ( nr_seq_lote_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

			
retorno_w		varchar(2);			


BEGIN

select		CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT		retorno_w
from		prescr_mat_hor a,
		prescr_material b,
		prescr_medica c,
		ap_lote_item d
where		a.nr_prescricao 	= b.nr_prescricao
and		a.nr_seq_material 	= b.nr_sequencia
and		c.nr_prescricao 	= b.nr_prescricao
and		a.nr_sequencia	= d.nr_seq_mat_hor
and		d.nr_seq_lote	= nr_seq_lote_p
and		c.cd_pessoa_fisica	= cd_pessoa_fisica_p;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
