CREATE OR REPLACE FUNCTION obter_se_pessoa_ocor_sac (cd_pessoa_fisica_p text, cd_cgc_p text, ie_tipo_ocor_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'N';
cont_w		bigint;

/* Tipo da ocorrência
T - Todos
E - Elogio
R - Reclamação
*/
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	cont_w
	FROM sac_boletim_ocorrencia a, sac_resp_bol_ocor b
LEFT OUTER JOIN sac_classif_ocorrencia c ON (b.nr_seq_classif = c.nr_sequencia)
WHERE b.nr_seq_bo		= a.nr_sequencia  and (c.ie_tipo_classif	= ie_tipo_ocor_p or ie_tipo_ocor_p = 'T') and a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	count(*)
	into STRICT	cont_w
	FROM sac_boletim_ocorrencia a, sac_resp_bol_ocor b
LEFT OUTER JOIN sac_classif_ocorrencia c ON (b.nr_seq_classif = c.nr_sequencia)
WHERE b.nr_seq_bo		= a.nr_sequencia  and (c.ie_tipo_classif	= ie_tipo_ocor_p or ie_tipo_ocor_p = 'T') and a.cd_cgc		= cd_cgc_p;

end if;

if (cont_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
