SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pessoa_proc_aval ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_resp_w					mat_avaliacao.cd_pessoa_resp%type;
ie_responsavel_w 					varchar(1) := 'N';
qt_registros_w					bigint;


BEGIN

select	cd_pessoa_resp
into STRICT	cd_pessoa_resp_w
from	mat_avaliacao
where	nr_sequencia = nr_sequencia_p;

if (cd_pessoa_resp_w = cd_pessoa_fisica_p) then
	ie_responsavel_w := 'S';
else
	select	count(*)
	into STRICT	qt_registros_w
	from	mat_avaliacao_setor
	where	cd_pessoa_avaliadora = cd_pessoa_fisica_p
	and	nr_seq_mat_aval = nr_sequencia_p;

	if (qt_registros_w > 0) then
		ie_responsavel_w := 'S';
	end if;
end if;



return	ie_responsavel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
