CREATE OR REPLACE FUNCTION obter_se_pessoa_recado (nr_seq_recado_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ie_recado_w	varchar(1) := 'S';


BEGIN 
if (nr_seq_recado_p IS NOT NULL AND nr_seq_recado_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	ie_recado_w 
	from	recado_medico 
	where	nr_sequencia = nr_seq_recado_p 
	and	((cd_medico = cd_pessoa_fisica_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p) or (obter_dados_usuario_opcao(nm_usuario_reg,'C') = cd_pessoa_fisica_p));
end if;
 
return ie_recado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
