CREATE OR REPLACE FUNCTION obter_se_pessoa_residente ( cd_pessoa_fisica_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_meses_residente_w 			bigint;
dt_chegada_brasil_w   			timestamp;
ie_retorno_w					varchar(1);


BEGIN

ie_retorno_w := 'N';

begin
	select 	max(dt_chegada_brasil)
	into STRICT	dt_chegada_brasil_w
	from   	pessoa_fisica
	where   cd_pessoa_fisica = cd_pessoa_fisica_p;
exception
when others then
	dt_chegada_brasil_w := null;
end;


if (dt_chegada_brasil_w IS NOT NULL AND dt_chegada_brasil_w::text <> '') then

	begin
		select	max(qt_meses_residente)
		into STRICT	qt_meses_residente_w
		from 	parametro_faturamento
		where	cd_estabelecimento = WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO;
	exception
	when others then
		qt_meses_residente_w := 0;
	end;

	if ( qt_meses_residente_w > 0) then
		if (pkg_date_utils.start_of(clock_timestamp(), 'DD', 0) > pkg_date_utils.start_of(PKG_DATE_UTILS.ADD_MONTH(dt_chegada_brasil_w, qt_meses_residente_w,0), 'DD', 0)) then
			ie_retorno_w := 'S';
		end if;
	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
