CREATE OR REPLACE FUNCTION obter_se_pessoa_substituta ( cd_cargo_aprov_p bigint, nm_usuario_aprov_p text, cd_solicitante_p text, dt_limite_p timestamp, nm_usuario_p text, cd_estab_p bigint default null, nr_seq_tipo_spa_p bigint default null, nr_seq_motivo_spa_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1);
ie_substituto_w				varchar(1);
cd_pessoa_substituta_w			varchar(10);
nm_usuario_substituto_w			varchar(15);


BEGIN

ds_retorno_w	:= 'N';

SELECT * FROM spa_obter_pessoa_substituta(cd_cargo_aprov_p, nm_usuario_aprov_p, cd_solicitante_p, dt_limite_p, ie_substituto_w, cd_pessoa_substituta_w, cd_estab_p, nr_seq_tipo_spa_p, nr_seq_motivo_spa_p) INTO STRICT ie_substituto_w, cd_pessoa_substituta_w;

if (coalesce(ie_substituto_w,'N') = 'S') then

	select	max(nm_usuario)
	into STRICT	nm_usuario_substituto_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_substituta_w;

	if (nm_usuario_substituto_w = nm_usuario_p) then
		ds_retorno_w	:= 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
