CREATE OR REPLACE FUNCTION obter_se_pessoa_titulo ( cd_pessoa_fisica_p text, cd_cgc_p text, nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
cd_pessoa_fisica_w	varchar(10);
cd_pessoa_fisica_ref_w	varchar(10);

c01 CURSOR FOR	/* responsáveis pela pessoa física */
SELECT	a.cd_pessoa_fisica_ref
from	compl_pessoa_fisica a
where	a.ie_tipo_complemento	in (3,4,5)
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w;


BEGIN

select	max(a.cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	titulo_receber a
where	a.nr_titulo	= nr_titulo_p;

if (cd_pessoa_fisica_w	= cd_pessoa_fisica_p) then
	ie_retorno_w	:= 'S';
else

	open	c01;
	loop
	fetch	c01 into
		cd_pessoa_fisica_ref_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if (cd_pessoa_fisica_ref_w	= cd_pessoa_fisica_p) then
			ie_retorno_w	:= 'S';
		end if;

	end	loop;
	close	c01;

end if;

RETURN ie_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
