CREATE OR REPLACE FUNCTION obter_se_pf_equipe_atend ( cd_medico_resp_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pf_equipe
where	nr_sequencia in (	SELECT	nr_sequencia
				from	pf_equipe
				where	cd_pessoa_fisica = cd_medico_resp_p
				
union all

				SELECT	nr_seq_equipe
				from	pf_equipe_partic
				where	cd_pessoa_fisica = cd_medico_resp_p)
and	nr_sequencia in (	select	nr_sequencia
				from	pf_equipe
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				
union all

				select	nr_seq_equipe
				from	pf_equipe_partic
				where	cd_pessoa_fisica = cd_pessoa_fisica_p);


if (qt_registro_w = 0) then
	return	'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
