CREATE OR REPLACE FUNCTION obter_se_pf_equipe_projeto ( nr_seq_projeto_p bigint, cd_pessoa_fisica_p text, ie_situacao_ativa_p text) RETURNS varchar AS $body$
DECLARE


ie_usuario_projeto_w	varchar(1);


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_usuario_projeto_w
	from	proj_equipe pe,
		proj_equipe_papel pep
	where	pep.nr_seq_equipe = pe.nr_sequencia
	and	pe.nr_seq_proj = nr_seq_projeto_p
	and	pep.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	((coalesce(ie_situacao_ativa_p,'N') = 'N') or (coalesce(pep.ie_situacao,'A') = 'A'));
	end;
end if;
return ie_usuario_projeto_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
