CREATE OR REPLACE FUNCTION obter_se_pf_possui_recoleta ( cd_pessoa_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_recoleta_w	varchar(1);

BEGIN

begin

select 	coalesce(CASE WHEN coalesce(a.nr_seq_doacao_amostra::text, '') = '' THEN 'N'  ELSE 'S' END ,'N')
into STRICT	ie_possui_recoleta_w
from	san_doacao a
where	a.nr_sequencia = (	SELECT 	max(b.nr_sequencia)
				from	san_doacao b
				where	b.cd_pessoa_fisica = cd_pessoa_p);
exception
	when others then
		ie_possui_recoleta_w	:= 'N';
end;

return	ie_possui_recoleta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
