SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pf_prontuario ( nm_campo_p text, vl_campo_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(1);
qt_pront_w		bigint;


BEGIN 
 
if (vl_campo_p IS NOT NULL AND vl_campo_p::text <> '') then 
	if (nm_campo_p = 'ATEND') then 
	 
		select	count(*) 
		into STRICT	qt_pront_w 
		from	same_prontuario a 
		where	a.cd_pessoa_fisica = substr(obter_pessoa_atendimento(vl_campo_p, 'C'),1,10);
	 
	elsif (nm_campo_p = 'PRONT') then 
	 
		select 	count(*) 
		into STRICT	qt_pront_w 
		from  	same_prontuario 
		where 	cd_pessoa_fisica = (	SELECT max(cd_pessoa_fisica)              
						from  pessoa_fisica                      
						where  nr_prontuario = vl_campo_p);
						 
	elsif (nm_campo_p = 'PROTOC') then 
 
		select	count(*) 
		into STRICT	qt_pront_w 
		from  same_prontuario a, 
			conta_paciente b,                       
			protocolo_convenio c, 
			atendimento_paciente d                      
		where  a.cd_pessoa_fisica = d.cd_pessoa_fisica 
		and	b.nr_seq_protocolo = c.nr_Seq_protocolo 
		and	d.nr_atendimento = b.nr_atendimento		           
		and   ((to_char(c.nr_seq_protocolo) = vl_campo_p) or (c.nr_protocolo = vl_campo_p));
						 
	elsif (nm_campo_p = 'CONTA') then 
 
		select	count(*) 
		into STRICT	qt_pront_w 
		from	same_prontuario a 
		where	a.cd_pessoa_fisica = (	SELECT 	obter_pessoa_atendimento(x.nr_atendimento, 'C') 
		 				from	conta_paciente x 
						where	x.nr_interno_conta = vl_campo_p);
 
	elsif (nm_campo_p = 'PRESCR') then 
	 
		select	count(*) 
		into STRICT	qt_pront_w 
		from	same_prontuario a 
		where	a.cd_pessoa_fisica = (	SELECT	max(x.cd_pessoa_fisica) 
						from	prescr_medica x 
						where	x.nr_prescricao = vl_campo_p);
	 
	end if;
	 
end if;	
 
if (qt_pront_w > 0) then 
	ds_retorno_w := 'S';
else 
	ds_retorno_w := 'N';	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_pf_prontuario ( nm_campo_p text, vl_campo_p text) FROM PUBLIC;

