CREATE OR REPLACE FUNCTION obter_se_philips_ou_terceiro ( nr_localizacao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
W - Philips
C - Terceiro
*/
ie_retorno	varchar(1);


BEGIN

	select	CASE WHEN ie_terceiro='N' THEN  'W'  ELSE 'C' END  ie_terceiro
	into STRICT	ie_retorno
	from	man_localizacao
	where	nr_sequencia = nr_localizacao_p;

return	ie_retorno;

end;
