CREATE OR REPLACE FUNCTION obter_se_pj_matriz_filial ( cd_cnpj_orig_p text, cd_cnpj_alt_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint;
ie_matriz_filial_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pessoa_juridica
where	cd_cgc = cd_cnpj_orig_p
and	substr(cd_cgc,1,8) = substr(cd_cnpj_alt_p,1,8);

if (qt_registros_w > 0) then
	ie_matriz_filial_w := 'S';
end if;

return	ie_matriz_filial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
