CREATE OR REPLACE FUNCTION obter_se_pj_pagto ( cd_cgc_p text, ie_forma_pagto_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ds_retorno_w
from	pessoa_juridica_pagto a
where	a.cd_cgc = cd_cgc_p;

if (ds_retorno_w = 'N') then
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	pessoa_juridica_pagto a
	where	a.cd_cgc = cd_cgc_p
	and	a.ie_forma_pagto = ie_forma_pagto_p;

	end;
end if;

return ds_retorno_w;

end;
$body$
