CREATE OR REPLACE FUNCTION obter_se_pls_mensalidade ( nr_seq_pagador_p bigint, dt_contratacao_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_existe_w	bigint := 0;


BEGIN

if (coalesce(nr_seq_pagador_p,0) > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	pls_mensalidade
	where	nr_seq_pagador = nr_seq_pagador_p
	and	trunc(dt_referencia,'month') = trunc(to_date(dt_contratacao_p,'dd/mm/yyyy'),'month');

end if;

return	qt_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
