CREATE OR REPLACE FUNCTION obter_se_possui_alerta (cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_alerta_w	varchar(2000);
dt_alerta_w	timestamp;
ds_retorno_w	varchar(20000);

c01 CURSOR FOR 
 
SELECT	a.dt_alerta, 
	a.ds_alerta 
from	Atendimento_Alerta a 
where	nr_atendimento	= nr_atendimento_p 
and	a.ie_situacao = 'A' 
and	((coalesce(a.dt_fim_alerta::text, '') = '') or (a.dt_fim_alerta >= trunc(clock_timestamp(),'dd'))) 
and	obter_se_tipo_alerta_lib(a.nr_seq_tipo_alerta,wheb_usuario_pck.get_nm_usuario)	= 'S' 

union all
 
SELECT	a.dt_alerta, 
	a.ds_alerta 
from	Alerta_paciente a 
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	a.ie_situacao = 'A' 
and	((coalesce(a.dt_fim_alerta::text, '') = '') or (a.dt_fim_alerta >= trunc(clock_timestamp(),'dd'))) 
and	obter_se_tipo_alerta_lib(a.nr_seq_tipo_alerta,wheb_usuario_pck.get_nm_usuario)	= 'S' 
order 	by 1, 2;


BEGIN 
 
ds_retorno_w := 'N';
 
open c01;
loop 
fetch 	c01 into 
  	dt_alerta_w, 
	ds_alerta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := 'S';
end loop;
close c01;
 
return substr(ds_retorno_w,1,255);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
