CREATE OR REPLACE FUNCTION obter_se_possui_atend_opme ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w	bigint;
ie_retorno_w	varchar(1);

C01 CURSOR FOR
	SELECT	nr_prescricao
	from	prescr_medica
	where	nr_atendimento = nr_atendimento_p
	order by 1;


BEGIN
open C01;
loop
fetch C01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND or ie_retorno_w = 'S';  /* apply on C01 */
	begin
	select	obter_se_possui_prescr_opme(nr_prescricao_w)
	into STRICT	ie_retorno_w
	;

	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
