SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_avaliacao (cd_pessoa_solicitante_p bigint) RETURNS varchar AS $body$
DECLARE


qt_os_sem_aval_w	integer;
qt_os_nao_liberada_w	integer;
qt_total_w	integer;
ie_consiste_w	varchar(1) := 'N';


BEGIN
--Verifica se o solicitante possui alguma OS sem avaliação ou se possui alguma não liberada.
select	count(*)
into STRICT	qt_os_sem_aval_w
from	man_ordem_servico a
where	(a.dt_fim_real IS NOT NULL AND a.dt_fim_real::text <> '')
and	a.ie_status_ordem		= '3'
and	a.cd_pessoa_solicitante	= cd_pessoa_solicitante_p
and not exists (	SELECT 1
		from	  man_ordem_serv_avaliacao b
		where  a.nr_sequencia = b.nr_seq_ordem_servico);

select	count(*)
into STRICT	qt_os_nao_liberada_w
from	man_ordem_servico a
where	(a.dt_fim_real IS NOT NULL AND a.dt_fim_real::text <> '')
and	a.ie_status_ordem		= '3'
and	a.cd_pessoa_solicitante	= cd_pessoa_solicitante_p
and exists (	SELECT	1
		from	man_ordem_serv_avaliacao b
		where	a.nr_sequencia = b.nr_seq_ordem_servico
		and	coalesce(b.dt_liberacao::text, '') = '');

qt_total_w := qt_os_nao_liberada_w;

if (coalesce(qt_total_w,0) > 0) then
   ie_consiste_w := 'S';
elsif (coalesce(qt_total_w,0) = 0) then
   ie_consiste_w := 'N';
end if;

return	ie_consiste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
