CREATE OR REPLACE FUNCTION obter_se_possui_comunic_cih ( nr_atendimento_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


qt_comunic_w	bigint;
ds_retorno_w	varchar(10);


BEGIN

select	count(*)
into STRICT 	qt_comunic_w
from	prescr_mat_comunic_cih c,
	prescr_material p,
	prescr_medica m
where	c.nr_prescricao 	= p.nr_prescricao
and	c.nr_seq_prescricao 	= p.nr_sequencia
and	p.nr_prescricao		= m.nr_prescricao
and	m.nr_atendimento	= nr_atendimento_p
and 	((coalesce(c.dt_ciente::text, '') = '')
and (coalesce(c.nr_seq_pres_mat_com::text, '') = '')
and (c.nr_sequencia not in (	SELECT 	nr_seq_pres_mat_com
				from	prescr_mat_comunic_cih c,
					prescr_material p,
					prescr_medica m
				where	c.nr_prescricao 	= p.nr_prescricao
				and	c.nr_seq_prescricao 	= p.nr_sequencia
				and	p.nr_prescricao		= m.nr_prescricao
				and	m.nr_atendimento	= nr_atendimento_p
				and 	(c.nr_seq_pres_mat_com IS NOT NULL AND c.nr_seq_pres_mat_com::text <> '')
				and 	obter_perm_vis_comunic_cih(cd_medico_p,c.nr_sequencia, c.nr_seq_tipo, c.nr_prescricao) = 'S')))
and 	obter_perm_vis_comunic_cih(cd_medico_p,c.nr_sequencia, c.nr_seq_tipo, c.nr_prescricao) = 'S';

if (qt_comunic_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
