CREATE OR REPLACE FUNCTION obter_se_possui_dietas (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



possui_dietas_w		varchar(1)	:= 'N';
qt_prescr_dieta_w	integer;
qt_rep_jejum_w		integer;


BEGIN

select	count(*)
into STRICT	qt_prescr_dieta_w
from 	prescr_dieta
where 	nr_prescricao = nr_prescricao_p;

select 	count(*)
into STRICT	qt_rep_jejum_w
from 	rep_jejum
where 	nr_prescricao = nr_prescricao_p;

if (qt_prescr_dieta_w = 0 and qt_rep_jejum_w = 0) then
	possui_dietas_w := 'S';
end if;

return	possui_dietas_w;

end;
$body$
LANGUAGE PLPGSQL
