CREATE OR REPLACE FUNCTION obter_se_possui_diluente ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_via_aplicacao_p text) RETURNS varchar AS $body$
DECLARE


qt_registro_w				smallint;
cd_material_generico_w		integer;
ie_gera_diluicao_w			varchar(1) := 'S';


BEGIN

select	coalesce(max(cd_material_generico), cd_material_p)
into STRICT	cd_material_generico_w
from	material
where	cd_material = cd_material_p;

if (ie_via_aplicacao_p IS NOT NULL AND ie_via_aplicacao_p::text <> '') then
	select	ie_gera_diluicao
	into STRICT	ie_gera_diluicao_w
	from	via_aplicacao
	where	ie_via_aplicacao = ie_via_aplicacao_p;
end if;

select	coalesce(sum(qt_registro) ,0)
into STRICT	qt_registro_w
from (
	SELECT	1 qt_registro
	from	material_diluicao
	where	ie_gera_diluicao_w 		= 'S'
	and	ie_reconstituicao		= 'N'
	and	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_material			= cd_material_p
	and	((ie_via_aplicacao		= ie_via_aplicacao_p) or (coalesce(ie_via_aplicacao::text, '') = ''))
	
union

	SELECT	1
	from	material_diluicao
	where	ie_gera_diluicao_w		= 'S'
	and	ie_reconstituicao		= 'N'
	and	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_material		= cd_material_generico_w
	and	((ie_via_aplicacao		= ie_via_aplicacao_p) or (coalesce(ie_via_aplicacao::text, '') = ''))) alias10;

if (qt_registro_w > 0) then
	return 'S';
else
	return 'N';
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
