CREATE OR REPLACE FUNCTION obter_se_possui_doenca_prev (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_doenca_w     bigint:=0;
ds_expressao_w  varchar(255):='';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')then
	select	count(*)
	into STRICT	ie_doenca_w
	from    paciente_antec_clinico
	where   cd_pessoa_fisica  = cd_pessoa_fisica_p
        and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and     coalesce(dt_inativacao::text, '') = '';
end if;

if (ie_doenca_w > 0)then
	ds_expressao_w :=	obter_desc_expressao(883834);
end if;

return	ds_expressao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
