CREATE OR REPLACE FUNCTION obter_se_possui_estagio (nr_regra_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_status_w STATUS_REGRA_ENTREGA.NR_SEQUENCIA%TYPE;
nr_count_w bigint;


BEGIN
select NR_SEQUENCIA
into STRICT nr_seq_status_w
from status_regra_entrega
where nr_seq_regra = nr_regra_p
and   ie_status_execucao = '40';

if (nr_seq_status_w IS NOT NULL AND nr_seq_status_w::text <> '') then
	select count(*)
	into STRICT nr_count_w
	from ESTAGIO_STATUS_ENTREGA
	where nr_seq_status = nr_seq_status_w;

	if ((nr_count_w IS NOT NULL AND nr_count_w::text <> '')
	   and nr_count_w > 0 ) then
	return 'S';

	end if;


end if;

return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
