CREATE OR REPLACE FUNCTION obter_se_possui_exame_aprovado ( nr_prescricao_p bigint, dt_aprovacao_p text) RETURNS varchar AS $body$
DECLARE



ie_resultado_w	varchar(1) := 'N';
qtd_w		bigint;


BEGIN

select  count(*)
into STRICT	qtd_w
from    exame_lab_result_item a,
        exame_lab_resultado b,
        prescr_medica c
where  	a.nr_seq_resultado 	= b.nr_seq_resultado
and     b.nr_prescricao 	= c.nr_prescricao
and     c.nr_prescricao 	= nr_prescricao_p
and     a.dt_aprovacao > to_date(dt_aprovacao_p,'dd/mm/yyyy');

if (qtd_w > 0) then
	ie_resultado_w := 'S';
end if;

return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
