CREATE OR REPLACE FUNCTION obter_se_possui_exame_dep ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_exame_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w	bigint;
cd_convenio_w			bigint;
nr_atendimento_w		bigint;
cd_setor_prescr_w		bigint;
nr_seq_material_w		bigint;
ie_possui_exame_dep_w	char(1);

/*ie_opcao_p
E - Exames e procedimentos de rotina
P - Prescrição
*/
BEGIN

Select	coalesce(max(nr_atendimento),0),
		max(cd_estabelecimento),
		max(cd_setor_atendimento)
into STRICT	nr_atendimento_w,
		cd_estabelecimento_w,
		cd_setor_prescr_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

cd_convenio_w := obter_convenio_atendimento(nr_atendimento_w);


if (ie_opcao_p = 'P') then
	select	coalesce(max(b.nr_sequencia),0)
	into STRICT	nr_seq_material_w
	from	prescr_procedimento a,
			material_exame_lab b
	where	a.cd_material_exame = b.cd_material_exame
	and		a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia = nr_sequencia_p;

	Select	coalesce(max('S'),'N')
	into STRICT	ie_possui_exame_dep_w
	from	exame_lab_dependente a where		a.nr_seq_exame = nr_seq_exame_p
	and 	coalesce(a.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and 	coalesce(a.cd_setor_prescricao,cd_setor_prescr_w) = cd_setor_prescr_w
	and 	coalesce(a.nr_seq_material_regra,nr_seq_material_w) = nr_seq_material_w
	and 	coalesce(a.cd_convenio,cd_convenio_w) = cd_convenio_w
	and 	coalesce(a.cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p
	and 	a.ie_geracao in (7, 9)
	and		not exists ( SELECT 1
						 from prescr_procedimento b
						 where b.nr_prescricao = nr_prescricao_p
						 and   b.nr_seq_superior = nr_sequencia_p
						 and   a.nr_seq_exame_dep = b.nr_seq_exame) LIMIT 1;
else

	Select	coalesce(max('S'),'N')
	into STRICT	ie_possui_exame_dep_w
	from	exame_lab_dependente a where		a.nr_seq_exame = nr_seq_exame_p
	and 	coalesce(a.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and 	coalesce(a.cd_setor_prescricao,cd_setor_prescr_w) = cd_setor_prescr_w
	and 	coalesce(a.cd_convenio,cd_convenio_w) = cd_convenio_w
	and 	coalesce(a.cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p
	and 	a.ie_geracao in (7, 9) LIMIT 1;

end if;


return	ie_possui_exame_dep_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
