CREATE OR REPLACE FUNCTION obter_se_possui_exame_restrito (nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_possui_w	bigint 	:= 0;
ie_resultado_w	varchar(1) 	:= 'N';

BEGIN
	Select  count(*)
	into STRICT	ie_possui_w
	from  	prescr_procedimento b,
        	prescr_medica c,
        	exame_lab_resultado e,
        	exame_lab_result_item g,
        	exame_laboratorio f
  	where  	b.nr_prescricao         = c.nr_prescricao
  	and    	b.nr_prescricao         = e.nr_prescricao
        and    	e.nr_seq_resultado      = g.nr_seq_resultado
        and    	g.nr_seq_exame          = f.nr_seq_exame
        and    	b.nr_seq_exame          = f.nr_seq_exame
  	and     g.nr_seq_prescr         = b.nr_sequencia
        and     f.ie_restricao <> 'N'
        and   	b.nr_prescricao         = nr_prescricao_p;

if (ie_possui_w  > 0) then
	ie_resultado_w := 'S';
end if;

return ie_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
