CREATE OR REPLACE FUNCTION obter_se_possui_ficha_diag ( nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


dt_diagnostico_w	timestamp;
cd_doenca_w		varchar(10);
qt_possui_ficha_w	bigint;
ds_retorno_w		varchar(1);

BEGIN
select	max(dt_diagnostico)
into STRICT 	dt_diagnostico_w
from 	diagnostico_medico a
where	nr_atendimento	= nr_atendimento_p
and	exists (	SELECT	1
			from	diagnostico_doenca x
			where	x.nr_atendimento	= a.nr_atendimento
			and	x.dt_diagnostico	= a.dt_diagnostico);
			
select  max(cd_doenca)
into STRICT	cd_doenca_w
from	diagnostico_doenca
where	nr_atendimento	= nr_atendimento_p
and	dt_diagnostico  = dt_diagnostico_w;

select	count(*)
into STRICT	qt_possui_ficha_w
from	notificacao_sinan a,
	cih_doenca_compulsoria b
where	a.nr_seq_doenca_compulsoria = b.nr_sequencia
and	a.cd_paciente = cd_pessoa_fisica_p
and	b.cd_doenca_cid = cd_doenca_w
and a.nr_atendimento = nr_atendimento_p;

if (qt_possui_ficha_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
