CREATE OR REPLACE FUNCTION obter_se_possui_hor_adm ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_w		varchar(1)	:= 'N';


BEGIN

if (ie_tipo_item_p	in ('IAG','IA','MAT','M','SNE','S','LD')) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_material = nr_seq_item_p
	and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

elsif (ie_tipo_item_p	in ('G','C','HM','I','P','L')) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_w
	from	prescr_proc_hor
	where	nr_prescricao 		= nr_prescricao_p
	and	nr_seq_procedimento 	= nr_seq_item_p
	and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

elsif (ie_tipo_item_p	in ('DI','SOL')) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_item_p
	and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

elsif (ie_tipo_item_p	in ('D')) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_w
	from	prescr_dieta_hor
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_dieta	= nr_seq_item_p
	and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

elsif (ie_tipo_item_p	in ('R')) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_w
	from	prescr_rec_hor
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_recomendacao	= nr_seq_item_p
	and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

end if;

return	ie_possui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
