SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_impedimento (nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
nr_seq_tipo_w	bigint;

BEGIN

select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from 	san_doacao
where	nr_sequencia = nr_seq_doacao_p;

-- Verifica se Ã© impedido definitivamente
SELECT   CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	 ds_retorno_w
FROM     san_impedimento a,
         san_doacao_impedimento b
WHERE    a.nr_sequencia       = b.nr_seq_impedimento
AND      a.ie_impede_doacao   = 'S'
AND      b.nr_seq_doacao      = nr_seq_doacao_p;


if (ds_retorno_w = 'N') then
	SELECT   CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	 ds_retorno_w
	FROM     san_impedimento a,
		 san_questionario b
	WHERE    a.nr_sequencia       = b.nr_seq_impedimento
	AND      a.ie_impede_doacao   = 'S'
	AND      b.nr_seq_doacao      = nr_seq_doacao_p
	and    coalesce(b.ie_impede_doacao,'N') = 'S';
end if;

if (ds_retorno_w = 'N') then
	SELECT   CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	 ds_retorno_w
	FROM     san_impedimento a,
		 san_questionario b,
		 san_regra_impedimento c
	WHERE    a.nr_sequencia = b.nr_seq_impedimento
	and	a.nr_sequencia = c.nr_seq_impedimento
	and	c.nr_seq_tipo = nr_seq_tipo_w
	AND      a.ie_impede_doacao = 'R'
	and 	c.ie_impede_doacao = 'S'
	AND      b.nr_seq_doacao      = nr_seq_doacao_p
	and    coalesce(b.ie_impede_doacao,'N') = 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_possui_impedimento (nr_seq_doacao_p bigint) FROM PUBLIC;

