SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_intermed_nf (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
cd_pessoa_fisica_w	nota_fiscal.cd_pessoa_fisica%type;
cd_cgc_w		nota_fiscal.cd_cgc%type;
cd_cgc_estab_w		estabelecimento.cd_cgc%type;
cd_cgc_conv_w		convenio.cd_cgc%type;
cd_convenio_w		convenio.cd_convenio%type;
ie_tipo_convenio_w	convenio.ie_tipo_convenio%type;


/*
Retorno
 S - Possui intermediário
 N - Não possui Intermediário
 --------------------------------------
 IE_TIPO_CONVENIO não permitidos para Intermediário
 Particular - 1
SUS           - 3
Próprio      - 5
IAMSPE   - 10

*/
BEGIN

begin

select	max(cd_cgc),
	max(cd_pessoa_fisica),
	obter_cgc_estabelecimento(max(cd_estabelecimento))
into STRICT	cd_cgc_w,
	cd_pessoa_fisica_w,
	cd_cgc_estab_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

select	coalesce(max(cd_convenio_parametro),0)
into STRICT	cd_convenio_w
from (SELECT		a.cd_convenio_parametro
	from		conta_paciente a,
			nota_fiscal b
	where		a.nr_interno_conta = b.nr_interno_conta
	and		b.nr_sequencia = nr_sequencia_p
	and		(b.nr_interno_conta IS NOT NULL AND b.nr_interno_conta::text <> '')
	
union all

	SELECT		a.cd_convenio cd_convenio_parametro
	from		protocolo_convenio a,
			nota_fiscal b
	where		a.nr_seq_protocolo = b.nr_seq_protocolo
	and		b.nr_sequencia = nr_sequencia_p
	and		(b.nr_seq_protocolo IS NOT NULL AND b.nr_seq_protocolo::text <> '')
	
union all

	select		a.cd_convenio cd_convenio_parametro
	from		lote_protocolo a,
			nota_fiscal b
	where		b.nr_seq_lote_prot	= a.nr_sequencia
	and		b.nr_sequencia		= nr_sequencia_p
	and		(b.nr_seq_lote_prot IS NOT NULL AND b.nr_seq_lote_prot::text <> '')) alias5;

exception
	when no_data_found then
		cd_convenio_w := 0;
end;


if (cd_convenio_w > 0) then
	begin

	select	max(cd_cgc),
		max(ie_tipo_convenio)
	into STRICT	cd_cgc_conv_w,
		ie_tipo_convenio_w
	from	convenio
	where	cd_convenio = cd_convenio_w;

	if not(ie_tipo_convenio_w in (1,3,5,10)) then
		begin
		ds_retorno_w := 'S';
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
