SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_maior_prior (nr_seq_status_filtro_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
qt_maior_prioridade_w	bigint;
qt_prioridade_filtro_w	bigint;
qt_status_codificacao_w bigint;

BEGIN

if ((coalesce(nr_seq_agenda_p,0) > 0) and (coalesce(nr_seq_status_filtro_p,0) > 0))then

	select count(*)
	into STRICT   qt_status_codificacao_w
	from   agenda_pac_codif_hist
	where  nr_seq_agenda = nr_seq_agenda_p
	and    nr_seq_status = nr_seq_status_filtro_p;

	if (qt_status_codificacao_w > 0) then


		select  max(nr_seq_prioridade)
		into STRICT	qt_maior_prioridade_w
		from 	status_codificacao
		where   nr_sequencia in (SELECT nr_seq_status
				 from agenda_pac_codif_hist
				 where nr_seq_agenda = nr_seq_agenda_p);

		if (coalesce(qt_maior_prioridade_w,0) > 0) then
			select  max(nr_seq_prioridade)
			into STRICT	qt_prioridade_filtro_w
			from    status_codificacao
			where   nr_sequencia = nr_seq_status_filtro_p;

			if (coalesce(qt_maior_prioridade_w,0) > coalesce(qt_prioridade_filtro_w,0)) then
				ds_retorno_w := 'S';
			else
				ds_retorno_w := 'N';
			end if;

		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
