CREATE OR REPLACE FUNCTION obter_se_possui_mat_med_assoc ( nr_prescricao_p bigint, nr_sequencia_proc_p bigint, nr_seq_proc_cpoe_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_possui_material	varchar(1) := 'N';


BEGIN

	if (coalesce(nr_seq_proc_cpoe_p,0) > 0) then

		select 	coalesce(max('S'), 'N')
		into STRICT	ie_possui_material
		from   	cpoe_procedimento
		where  	nr_sequencia =	nr_seq_proc_cpoe_p
		and		(coalesce(cd_mat_proc1, cd_mat_proc2, cd_mat_proc3, cd_mat_proc4, cd_mat_proc5, cd_mat_proc6, cd_mat_proc7) IS NOT NULL AND (coalesce(cd_mat_proc1, cd_mat_proc2, cd_mat_proc3, cd_mat_proc4, cd_mat_proc5, cd_mat_proc6, cd_mat_proc7))::text <> '');

	else

	    select 	coalesce(max('S'), 'N')
		into STRICT	ie_possui_material
		from   	prescr_material
		where  	nr_prescricao = nr_prescricao_p
		and    	nr_sequencia_proc = nr_sequencia_proc_p;

	end if;

return	ie_possui_material;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
