CREATE OR REPLACE FUNCTION obter_se_possui_medic_restrito ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_restrito_w	bigint;


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_restrito_w
from	material t,
	prescr_material b
where	b.cd_material 	= t.cd_material
and	b.nr_prescricao	= nr_prescricao_p
and	coalesce(t.ie_restrito,'N') = 'S';

if (qt_restrito_w 	> 0) 	then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
