CREATE OR REPLACE FUNCTION obter_se_possui_modelo (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(1);


BEGIN

select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from    RP_PAC_MODELO_AGENDAMENTO a,
	rp_tratamento b,
	rp_paciente_reabilitacao c
where 	a.NR_SEQ_PAC_REAB  = c.nr_sequencia
and   	b.NR_SEQ_PAC_REAV  = c.nr_sequencia
and   	b.NR_SEQ_PAC_REAV = nr_sequencia_p
and	a.ie_situacao <> 'I';

if (ds_retorno_w = 'N') then
	select	CASE WHEN count(*)=0 THEN 'V'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	rp_pac_modelo_agendamento
	where	nr_seq_pac_reab = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
