CREATE OR REPLACE FUNCTION obter_se_possui_motivo_alt_cir (ie_tipo_motivo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_regra_w	bigint;


BEGIN

if (ie_tipo_motivo_p IS NOT NULL AND ie_tipo_motivo_p::text <> '') then
	SELECT 	COUNT(*)
	into STRICT 	qt_regra_w
	FROM   	cirurgia_motivo_alteracao a,
		cirurgia_mot_alter_tipo b
	WHERE 	b.nr_seq_motivo = a.nr_sequencia
	AND	 b.ie_tipo  = ie_tipo_motivo_p
	and	a.ie_situacao = 'A';

	if (qt_regra_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
