CREATE OR REPLACE FUNCTION obter_se_possui_nao_cotado (nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


/*Retorna se a cotação possui ou não itens não cotados.*/

ie_retorno_w		varchar(1) := 'N';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_forn_item a,
       	cot_compra_item b
where	a.nr_cot_compra		= b.nr_cot_compra
and	a.nr_item_cot_compra	= b.nr_item_cot_compra
and	b.nr_cot_compra		= nr_cot_compra_p
and not exists (
	SELECT	d.nr_cot_compra
	from	cot_compra_forn_item d
	where	d.nr_cot_compra		= a.nr_cot_compra
	and	d.nr_item_cot_compra	= a.nr_item_cot_compra
	and	d.vl_unitario_material	<> 0);

if (qt_existe_w > 0) then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
