CREATE OR REPLACE FUNCTION obter_se_possui_nf_solic ( nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ie_possui_nf_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.ie_situacao = '1'
and	b.nr_solic_compra = nr_solic_compra_p;

if (qt_registro_w > 0) then
	ie_possui_nf_w := 'S';
else
	ie_possui_nf_w := 'N';
end if;


return	ie_possui_nf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
