CREATE OR REPLACE FUNCTION obter_se_possui_orca_pac (cd_pessoa_fisica_p text, dt_entrada_p timestamp, qt_dias_p bigint, ie_perm_vinc_orca_p text, ie_tipo_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_orcamento_w	bigint := 0;				
				 

BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	select coalesce(max(nr_seq),0) 
	into STRICT	nr_seq_orcamento_w 
	from ( 
	SELECT	coalesce(max(1),0) nr_seq 
	from	orcamento_paciente a 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.ie_status_orcamento = 2 
	and	coalesce(a.dt_validade,dt_entrada_p) > dt_entrada_p 
	and	coalesce(a.nr_atendimento::text, '') = '' 
	and	((qt_dias_p = 0) or (dt_orcamento > clock_timestamp() - qt_dias_p)) 
	and	ie_perm_vinc_orca_p <> 'I' 
	and	obter_se_conv_vinculacao_orc(a.cd_convenio,obter_estabelecimento_ativo) = 'S' 
	and  Possui_vinc_orc(ie_tipo_atendimento_p, a.ie_tipo_atendimento) = 'S' 
	
union
 
	SELECT	coalesce(max(1),0) nr_seq 
	from	orcamento_paciente a 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	coalesce(a.nr_atendimento::text, '') = '' 
	and	((qt_dias_p = 0) or (dt_orcamento > clock_timestamp() - qt_dias_p)) 
	and	ie_perm_vinc_orca_p = 'I' 
	and	obter_se_conv_vinculacao_orc(a.cd_convenio,obter_estabelecimento_ativo) = 'S' 
	and  Possui_vinc_orc(ie_tipo_atendimento_p, a.ie_tipo_atendimento) = 'S' 
	and (nr_sequencia_orcamento IS NOT NULL AND nr_sequencia_orcamento::text <> '') 
	order by 1 desc) alias23;
end if;
 
return	nr_seq_orcamento_w;
 
end;
$body$
