CREATE OR REPLACE FUNCTION obter_se_possui_orc_pac_mat ( nr_seq_orcamento_p bigint) RETURNS varchar AS $body$
DECLARE

ie_possui_mat_w	varchar(1);

BEGIN
if (nr_seq_orcamento_p IS NOT NULL AND nr_seq_orcamento_p::text <> '') then
	begin
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_mat_w
	from	orcamento_paciente_mat
	where	nr_sequencia_orcamento = nr_seq_orcamento_p
	and	coalesce(IE_ADICIONAL,'N') = 'S';
	end;
end if;
return ie_possui_mat_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
