SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_possui_ordem ( nr_seq_etiqueta_p bigint, nr_seq_processo_p bigint, nr_seq_area_prep_p bigint) RETURNS bigint AS $body$
DECLARE



qt_dose_w				prescr_material.qt_dose%type;
cd_unidade_medida_w		unidade_medida.cd_unidade_medida%type;
cd_material_w			material.cd_material%type;
nr_seq_etiqueta_w		adep_processo_frac.nr_sequencia%type;
nr_seq_etiqueta_real_w	adep_processo_frac.nr_sequencia%type;
ie_possui_etiqueta_w	char(1);


C01 CURSOR FOR
	SELECT 	b.qt_dose,
			b.cd_unidade_medida,
			b.cd_material,
			a.nr_seq_etiqueta
	from    prescr_material b,
			prescr_mat_hor a
	where   b.nr_prescricao            	= a.nr_prescricao
	and     b.nr_sequencia             	= a.nr_seq_material
	and     coalesce(coalesce(b.dt_suspensao,a.dt_suspensao)::text, '') = ''
	and    	coalesce(a.nr_seq_superior::text, '') = ''
	and     coalesce(b.ie_regra_disp,'X')   	<> 'N'
	and     a.nr_seq_processo = nr_seq_processo_p
	and     ((nr_seq_area_prep_p = 0) or (a.nr_seq_area_prep = nr_seq_area_prep_p))
	and		Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S';

C02 CURSOR FOR
	SELECT 	b.qt_dose,
			b.cd_unidade_medida,
			b.cd_material
	from    prescr_material b,
			prescr_mat_hor a
	where   b.nr_prescricao            	= a.nr_prescricao
	and     b.nr_sequencia             	= a.nr_seq_material
	and     coalesce(coalesce(b.dt_suspensao,a.dt_suspensao)::text, '') = ''
	and    	(a.nr_seq_superior IS NOT NULL AND a.nr_seq_superior::text <> '')
	and     coalesce(b.ie_regra_disp,'X')   	<> 'N'
	and		a.nr_seq_etiqueta = nr_seq_etiqueta_w
	and     ((nr_seq_area_prep_p = 0) or (a.nr_seq_area_prep = nr_seq_area_prep_p))
	and		Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S';


BEGIN

if (coalesce(nr_seq_processo_p,0) > 0) and (coalesce(nr_seq_etiqueta_p,0) > 0) then
	-- Aqui é descompactado o código digitado da etiqueta X(9) ______________(Etiqueta) X(Dígito verificador)
	nr_seq_etiqueta_real_w	:= obter_etiqueta_barras_frac(nr_seq_etiqueta_p);

	open C01;
	loop
	fetch C01 into
		qt_dose_w,
		cd_unidade_medida_w,
		cd_material_w,
		nr_seq_etiqueta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_possui_etiqueta_w
		from    prescr_material d,
				prescr_mat_hor b,
				adep_processo_frac a
		where   b.nr_seq_etiqueta 	= a.nr_sequencia
		and		coalesce(b.nr_seq_superior::text, '') = ''
		and     a.nr_sequencia		= nr_seq_etiqueta_real_w
		and     b.nr_prescricao   	= d.nr_prescricao
		and     b.nr_seq_material 	= d.nr_sequencia
		and		b.qt_dose			= qt_dose_w
		and		b.cd_unidade_medida = cd_unidade_medida_w
		and		b.cd_material	 	= cd_material_w LIMIT 1;

		if (ie_possui_etiqueta_w = 'S') then
			open C02;
			loop
			fetch C02 into
					qt_dose_w,
					cd_unidade_medida_w,
					cd_material_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin
					select	coalesce(max('S'),'N')
					into STRICT	ie_possui_etiqueta_w
					from    prescr_material d,
							prescr_mat_hor b,
							adep_processo_frac a
					where   b.nr_seq_etiqueta 	= a.nr_sequencia
					and		(b.nr_seq_superior IS NOT NULL AND b.nr_seq_superior::text <> '')
					and     a.nr_sequencia		= nr_seq_etiqueta_real_w
					and     b.nr_prescricao   	= d.nr_prescricao
					and     b.nr_seq_material 	= d.nr_sequencia
					and		b.qt_dose			= qt_dose_w
					and		b.cd_unidade_medida = cd_unidade_medida_w
					and		b.cd_material	 	= cd_material_w LIMIT 1;

					if (ie_possui_etiqueta_w = 'N') then
						return 0;
					end if;

				end;
			end loop;
			close C02;

			return nr_seq_etiqueta_w;
		end if;
		end;
	end loop;
	close C01;
end if;

return 0;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_possui_ordem ( nr_seq_etiqueta_p bigint, nr_seq_processo_p bigint, nr_seq_area_prep_p bigint) FROM PUBLIC;

